import { useNavigate } from "react-router-dom";

import Button from "./button.component";
import kakao from "../icons/kakao.png";
import styled from "styled-components";

const Wrapper = styled.div`
  text-align:center;
  width: 100%;
`

const KakaoButton = styled(Button)`
  display: flex;
  width: 350px;
  height: 48px;
  padding: 12px 40px;
  justify-content: center;
  align-items: center;
  flex-shrink: 0;
  border-radius: 12px;
  background: #fee500;
  border: none;
  color: #3a3a3a;
  font-size: 16px;
  font-style: normal;
  font-weight: 500;
  line-height: normal;
  letter-spacing: -0.32px;
`;

const KakaoLogin = () => {
  const navigate = useNavigate();
  const link = "http://43.200.203.22:8080/oauth2/authorization/kakao";

  const loginHandler = () => {
    window.location.href = link;
  };

  return (
  <Wrapper>
    <KakaoButton onClick={loginHandler}><img src={kakao} />카카오 로그인</KakaoButton>
    </Wrapper>);
};

export default KakaoLogin;
