import { ReactElement, createContext, useState, useMemo } from 'react';

interface ModalsProviderProps {
    children: ReactElement | ReactElement[]
}

interface ModalsStateType {
    Component: ReactElement,
    props: { [key:string]: any},
}

export const ModalsDispatchContext = createContext<{
    open: (Component: ReactElement, props: { [key:string]: any}) => void;
    close: (Component: ReactElement) => void;
}>({
    open: () => {return;},
    close:() => {return;}
});

export const ModalsStateContext = createContext<ModalsStateType[] | null>(null)

const ModalsProvider = ({children}:ModalsProviderProps) => {
    const [isModalOpen, setIsModalOpen] = useState<ModalsStateType[]>([]);

    const openModal = (
        Component: ReactElement,
        props: { [key:string]: any},
    ) => {
        setIsModalOpen(modals => {
            return [...modals, {Component, props}];
        });
    };

    const closeModal = () => {
        setIsModalOpen(false)
    }

    const dispatch = { openModal, closeModal };

    return (
        <ModalsStateContext.Provider value={openedModals}>
            <ModalsDispatchContext.Provider value={dispatch}>
                {children}
            </ModalsDispatchContext.Provider>
        </ModalsStateContext.Provider>
    )
}

export default ModalsProvider;

