import { useState, useContext, useEffect } from "react";

import { hours, minutes } from "../utils/select-options";
import {
  DispatchContext,
  WakeUpTimeContext,
} from "../contexts/wakeupTimeReducer.context";
import { ReactComponent as Expand } from "../icons/Expand.svg";

import styled, { css } from "styled-components";

const ContentsBox = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Wrapper = styled.div`
  position: relative;
  display: flex;
  justify-content:center;
  width: fit-content;
  height: fit-content;
  background: #fff;
  border-radius: 4px;
`;

const SelectBox = styled.select`
  display: flex;
  width: 220px;
  height: 40px;
  padding: 8px 16px;
  justify-content: center;
  align-items: center;
  gap: 240px;
  border-radius: 4px;
  border: 1px solid var(--sleeper-blue-2, #6b85f7);
  background: transparent;
  -webkit-appearance: none;
  z-index: 4;
`;

const ButtonBox = styled.div`
  display: flex;
  width: fit-content;
  text-align: center;
`;

const MDbox = styled.div`
  display: flex;
  padding: 8px 16px;
  justify-content: center;
  align-items: center;
  gap: 10px;
  border-radius: 8px;
  background: var(--sleeper-purple-1, #734ef7);
`;

const WakeupTime = () => {
  const { hour, minute, md } = useContext(WakeUpTimeContext);
  const dispatch = useContext(DispatchContext);
  if (!dispatch) throw new Error("dispatch is null");

  const [selected, setSelected] = useState({
    selectHour: hour,
    selectMinute: minute,
    selectMd: md,
  });
  const { selectHour, selectMinute, selectMd } = selected;

  const handleSelect = (e: any) => {
    setSelected({ ...selected, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    dispatch({ type: "update", payload: selected });
  }, [selected]);

  return (
    <ContentsBox>
      <Wrapper>
        <SelectBox onChange={handleSelect} name="selectHour" value={selectHour}>
          {hours.map((item) => (
            <option value={item} key={item}>
              {`${item}시`}
            </option>
          ))}
        </SelectBox>
        <Expand />
      </Wrapper>

      <Wrapper>
        <SelectBox
          onChange={handleSelect}
          name="selectMinute"
          value={selectMinute}
        >
          {minutes.map((item) => (
            <option value={item} key={item}>
              {`${item}분`}
            </option>
          ))}
        </SelectBox>
        <Expand />
      </Wrapper>

      <ButtonBox>
        <MDbox
          className="am"
          onClick={() => setSelected({ ...selected, selectMd: "AM" })}
        >
          AM
        </MDbox>
        <MDbox
          className="pm"
          onClick={() => setSelected({ ...selected, selectMd: "PM" })}
        >
          PM
        </MDbox>
      </ButtonBox>
    </ContentsBox>
  );
};

export default WakeupTime;
