import styled from "styled-components";
import { useModal } from "../hooks/useModal";

interface modalProps {
  isModalOpen: boolean;
  close(): void;
  label: string;
  additional?: any;
}

const Body = styled.div`
    
`

const Contents = styled.h6`
  font-size: 20px;
  font-style: normal;
  font-weight: 500;
  line-height: normal;
  letter-spacing: -0.4px;
  text-align: center;
  margin-top: 36px;
  white-space: pre-wrap;
  color: var(--gray-1, #4D4D4D);
`;


const Modal = ({ isModalOpen, additional, label }: modalProps) => {
  return (
  <div>
    {isModalOpen && 
        <Wrapper>
            <Body>
            <Contents>{label.split("\n").map((letter)=>(<>{letter}<br /></>))}</Contents>
        {additional ? <Contents>{additional}</Contents> : null}
            </Body>
            <Footer>
            <ModalButton className="close" onClick={close}>
          확인
        </ModalButton>
            </Footer>
        </Wrapper>}
  </div>
  );
};

export default Modal;
