import { ReactComponent as Up } from "../icons/Expand_Up.svg";
import { ReactComponent as Down } from "../icons/Expand_Down.svg";
import { ReactNode, useContext, useEffect, useState, useRef } from "react";
import {
  DispatchContext,
  WakeUpTimeContext,
} from "../contexts/wakeupTime.context";

import styled from "styled-components";

const Wrapper = styled.div`
  display: flex;
`;

const ButtonBox = styled.div`
  display: flex;
  flex-direction: column;
  input {
    width: 56px;
  }
`;

const MDbox = styled.div`
  background-color: white;
  display: flex;
  width: 56px;
  padding: 12px 16px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 10px;
  border-radius: 4px;
  border: 0.4px solid var(--gray-1, #4d4d4d);
`;

interface MiniButtonProps {
  type: ReactNode;
  onClick(): void;
  name: string;
}

const MiniButton = ({ name, type, onClick }: MiniButtonProps) => {
  return (
    <button name={name} onClick={onClick}>
      {type}
    </button>
  );
};

const WakeupTimeSetting = () => {
  const dispatch = useContext(DispatchContext);
  if (!dispatch) throw new Error("dispatch is null");

  const DEFAULT = {
    hour:8, minute:0, md:"AM"
  }
  const [time, setTime] = useState(DEFAULT);
  const { hour, minute, md } = time;

  /* const { hour, minute, md } = useContext(WakeUpTimeContext);

  useEffect(() => {
    console.log("re-rendered");
  }, [hour, minute, md]); */

  const onChange = (e:any) => {
    setTime({...time, [e.target.name]:e.target.value})
  }

  return (
    <Wrapper>
      <ButtonBox>
        <MiniButton
          name="up"
          type={<Up />}
          onClick={() => dispatch({ type: "HOUR_INCREASE" })}
        />
        <input name="hour" type="number" value={hour} onChange={onChange}></input>
        <MiniButton
          name="down"
          type={<Down />}
          onClick={() => dispatch({ type: "HOUR_DECREASE" })}
        />
      </ButtonBox>

      <ButtonBox>
        <MiniButton
          name="up"
          type={<Up />}
          onClick={() => dispatch({ type: "MINUTE_INCREASE" })}
        />
        <input name="minute" type="number" value={minute}></input>
        <MiniButton
          name="down"
          type={<Down />}
          onClick={() => dispatch({ type: "MINUTE_DECREASE" })}
        />
      </ButtonBox>

      <ButtonBox>
        <MDbox onClick={() => dispatch({ type: "CHANGE_TO_AM" })}>AM</MDbox>
        <MDbox onClick={() => dispatch({ type: "CHANGE_TO_PM" })}>PM</MDbox>
      </ButtonBox>
    </Wrapper>
  );
};

export default WakeupTimeSetting;
